mapper.select:
select a.bizrno, a.cmpnm, a.trmnl_no, a.pg_trmnl_no, a.pg_van, a.pay_bprprr, a.pay_telno, a.pay_cmpnm, a.pay_bizrno, a.pay_bplc , a.m_telno, a.email, a.bank_name, a.bank_serial, a.create_dt, a.bank_user, a.dealer_rate, a.member_input_yn, a.member_rate, a.memo, a.history_memo , a.join_amt, a.limit_one, a.limit_day, a.limit_month, a.limit_year, a.installment_months , a.biz_limit_one, a.biz_limit_day, a.biz_limit_month, a.biz_limit_year, a.biz_installment_months , a.pg_gb, a.van_gb, a.biz_type , b.usid, b.mber_code, b.biz_code, a.bprprr, a.bplc, a.biz_telno, a.pymnt_rate, a.dealer_id, a.dt, a.trget_biz_code, a.recommend_biz_code, a.baro_rate, a.etc_rate, a.dealer_kind, (SELECT code_nm FROM code WHERE group_nm = '대리점구분' AND sn = nvl(a.dealer_kind, (SELECT MAX(sn) FROM code WHERE group_nm = '대리점구분'))) as gubun, (SELECT cmpnm FROM biz WHERE biz_code = a.trget_biz_code) as target, (SELECT cmpnm FROM biz WHERE biz_code = a.recommend_biz_code) as recommend, GET_AGENCY(a.trget_biz_code, a.dealer_kind) as agent, GET_DISTRIBUTOR(a.trget_biz_code, a.dealer_kind) as distributor, (SELECT COUNT(*) FROM mber WHERE biz_code=b.biz_code AND author_code NOT IN('AUTHOR_MNGR','AUTHOR_DEALER')) AS mber_cnt, Nvl((SELECT hist_cn FROM mber_hist2 WHERE mber_code=b.mber_code AND sn=(SELECT MAX(sn) FROM mber_hist2 WHERE mber_code=b.mber_code AND hist_code='MH_USE_AT')) , 'N') as use_at, (SELECT creat_dt FROM mber_hist2 WHERE mber_code=b.mber_code AND sn=(SELECT MAX(sn) FROM mber_hist2 WHERE mber_code=b.mber_code AND hist_code='MH_USE_AT')) as creat_dt FROM biz a, mber b WHERE a.biz_code=b.biz_code
AND A.cmpnm= #{cmpnm}
AND A.dealer_id= #{dealerId}
AND b.use_at= #{useAt}
AND b.bizrno= #{bizNum}
AND b.author_code in('AUTHOR_MNGR','AUTHOR_DEALER') AND a.biz_code in (SELECT biz_code FROM biz START WITH biz_code = #{memberBizeCode} CONNECT BY NOCYCLE PRIOR biz_code = trget_biz_code) ORDER BY b.biz_code DESC

mapper.update:
UPDATE BIZ SET BIZRNO = #{bizrno}, DEALER_ID = #{dealerId}, CMPNM = #{cmpnm}, BPRPRR = #{bprprr}, BPLC = #{bprprr} , BIZ_TELNO = #{bizTelno},
TRMNL_NO = #{trmnlNo}, PG_TRMNL_NO = #{pgTrmnlNo}, PG_VAN = #{pgVan}, PG_GB = #{pgGb}, VAN_GB = #{vanGb}, BIZ_TYPE = #{bizType},
DEALER_KIND = #{dealerKind}, TRGET_BIZ_CODE = #{trgetBizCode}, RECOMMEND_BIZ_CODE = #{recommendBizCode}, PAY_BPRPRR = #{payBprprr}, PAY_TELNO = #{payTelno}, PAY_CMPNM = #{payCmpnm}, PAY_BIZRNO = #{payBizrno}, PAY_BPLC = #{payBplc}, M_TELNO = #{mTelno}, EMAIL = #{email}, BANK_NAME = #{bankName}, BANK_SERIAL = #{bankSerial}, BANK_USER = #{bankUser}, DEALER_RATE = #{dealerRate}, MEMBER_INPUT_YN = #{memberInputYn}, MEMBER_RATE = #{memberRate}, MEMO = #{memo}, HISTORY_MEMO = #{historyMemo}, LIMIT_ONE = #{limitOne}, LIMIT_DAY = #{limitDay}, LIMIT_MONTH = #{limitMonth}, LIMIT_YEAR = #{limitYear}, INSTALLMENT_MONTHS = #{installmentMonths}, BIZ_LIMIT_ONE = #{bizLimitOne}, BIZ_LIMIT_DAY = #{bizLimitDay}, BIZ_LIMIT_MONTH = #{bizLimitMonth}, BIZ_LIMIT_YEAR = #{bizLimitYear}, BIZ_INSTALLMENT_MONTHS = #{bizInstallmentMonths}, JOIN_AMT = #{joinAmt}, DT = SYSDATE WHERE BIZ_CODE = #{bizCode}

mapper.select:
SELECT nvl(min(dealer_kind), 0) as minDealerKind
FROM ( SELECT biz_code, nvl(dealer_kind, (SELECT max(sn) FROM code WHERE group_nm = '대리점구분')) as dealer_kind FROM biz START WITH biz_code =  #{bizCode} CONNECT BY NOCYCLE PRIOR biz_code = trget_biz_code ) a 
WHERE a.biz_code != #{val}
