	AND d.biz_code in 
	 	(SELECT aaa.biz_code FROM biz aaa START WITH aaa.biz_code = #{memberBizeCode}
	 	CONNECT BY NOCYCLE PRIOR aaa.biz_code = aaa.trget_biz_code)
	 AND b.confm_dt between #{dateStart} and #{dateEnd}


	     AND c.biz_code=#{bizCode}


     	AND b.delng_se_code= #{delngSeCode}


		AND e.calculate_type= #{calculateType}



		AND e.mber_name like '%'||#{mberName}||'%'


		AND b.confm_no= #{confmNo}


		AND e.induty_id= #{mberId}


		AND e.biz_num like '%'||#{bizNum}||'%'


		AND a.issue_cmpny_nm like '%'||#{issueCmpnyNm}||'%'


		AND b.splpc+b.vat= #{tot}


		AND	a.card_no IN 
		( 
			SELECT card_no from  
			    ( 
		        SELECT bbi.card_no, count(bbi.card_no) as cnt 
		        FROM delng aai, delng_credt bbi  
		        WHERE aai.rcipt_no = bbi.rcipt_no  
		        	AND aai.mber_code = bbi.mber_code
					AND aai.confm_dt between #{dateStart} and #{dateEnd}
		        	AND aai.delng_se_code='CARD_ISSUE'  
		        	AND aai.splpc + aai.vat >= '300000'  
		        GROUP BY bbi.card_no 
			    ) 
		    WHERE cnt > 1 
		)

mapper.select:
select * from ( SELECT ROW_NUMBER()
OVER( order by ${orderbyColumn} ${orderby} )
OVER(ORDER BY confm_dt DESC)
AS rnum, info.* FROM ( SELECT d.cmpnm, e.adres, e.biz_type_name, e.biz_item, d.trmnl_no, d.pg_trmnl_no, d.pg_van, c.usid, case e.pay_type when 'N' then '일반' else ( case e.pay_type when 'B' then '바로정산' else '익일입금' end ) end as pay_type, b.rcipt_no, (SELECT adi_cn FROM delng_adi WHERE mber_code=b.mber_code AND mber_code_sn=b.mber_code_sn AND rcipt_no=b.rcipt_no AND adi_code='PURCHSR_MBTLNUM') AS purchsr_mbtlnum, b.splpc, b.vat, b.delng_se_code, a.card_type, a.instlmt_month, a.issue_cmpny_nm, a.card_no, a.pg_van_gb, a.van_pg_comp, TO_DATE( b.confm_dt || b.confm_time , 'YYYYMMDDHH24MISS') as confm_dt,
b.confm_no, b.sms_auth_number, b.good_nm, b.payment_device, (SELECT mbtlnum FROM mber_basis WHERE mber_code=c.mber_code) AS mbtlnum, TO_DATE((SELECT confm_dt || confm_time FROM delng WHERE trget_mber_code=b.mber_code AND trget_mber_code_sn=b.mber_code_sn AND trget_rcipt_no=b.rcipt_no AND delng_se_code IN('CARD_CNCL','CASH_RCIPT_CNCL')), 'YYYYMMDDHH24MISS') AS cnclConfmDt, b.bigo, e.mber_name, e.company_name, e.biz_num, e.mber_mobile, e.mber_phone, e.calculate_type FROM delng b left outer join delng_credt a on a.mber_code=b.mber_code AND a.mber_code_sn=b.mber_code_sn AND a.rcipt_no=b.rcipt_no inner join mber c on b.mber_code=c.mber_code inner join biz d on c.biz_code=d.biz_code right outer join mber_basis e on c.mber_code =e.mber_code WHERE b.delng_se_code IN('CARD_ISSUE','CARD_CNCL','CASH_RCIPT_CNCL','CASH_RCIPT_ISSUE')
) info ) aa WHERE rnum BETWEEN #{startIndex} AND #{endIndex}

select * from (SELECT ROW_NUMBER()
OVER( order by ${orderbyColumn} ${orderby} )
OVER(ORDER BY confm_dt DESC)
as RNUM , aa.* from (select d.cmpnm , e.adres , e.biz_type_name , e.biz_item , d.trmnl_no , d.pg_trmnl_no , d.pg_van , c.usid , case e.pay_type when 'N' then '일반' else ( case e.pay_type when 'B' then '바로정산' else '익일입금' end ) end as pay_type , b.TRGET_RCIPT_NO as RCIPT_NO ,(SELECT adi_cn FROM delng_adi WHERE mber_code=b.mber_code AND mber_code_sn=b.mber_code_sn AND rcipt_no=b.TRGET_RCIPT_NO AND adi_code='PURCHSR_MBTLNUM') AS purchsr_mbtlnum , b.SPLPC , b.VAT , a.card_type , a.instlmt_month , a.issue_cmpny_nm , a.card_no , a.pg_van_gb , a.van_pg_comp , b.confm_no , b.sms_auth_number , b.good_nm , b.payment_device , e.mbtlnum ,b.CONFM_DT || b.CONFM_TIME as cnclConfmDt , (select CONFM_DT || CONFM_TIME from delng dd where dd.delng_se_code IN('CARD_ISSUE','CASH_RCIPT_ISSUE') and dd.MBER_CODE = b.MBER_CODE and dd.RCIPT_NO = b.TRGET_RCIPT_NO ) as CONFM_DT , b.delng_se_code , b.BIGO , e.mber_name , e.company_name , e.biz_num , e.mber_mobile , e.mber_phone , e.calculate_type from delng b inner join MBER_BASIS e on e.MBER_CODE = b.MBER_CODE inner join mber c on c.MBER_CODE = e.MBER_CODE inner join biz d on d.biz_code = c.biz_code left join delng_credt a on a.mber_code=b.mber_code AND a.mber_code_sn=b.mber_code_sn AND b.TRGET_RCIPT_NO=a.rcipt_no where b.delng_se_code IN('CARD_CNCL','CASH_RCIPT_CNCL')
) aa ) aaa WHERE rnum BETWEEN #{startIndex} AND #{endIndex}
